import React from "react";
import moment from "moment";
import styled from "styled-components";

////styled-components////

///colors////
const colorblue = "#28a5ff";
const colorwhite = "#ffffff";
const colorwhitegray = "#f4f4f4";
const colorgray = "#bcbcbc";
const colorgraydark = "#eaeaea";
const MovieImg = styled.div`
  height: 27.27vw;
  border-radius: 0.5rem;
  width: 100%;
  background-size: cover;
  background-position: center;
  position: relative;
  transition: all 0.5s;
`;

const MovieVote = styled.span`
  background: ${(props) => (props.movie__vote = colorblue)};
  border-radius: 0.5rem;
  color: ${(props) => (props.movie__vote = colorwhite)};
  font-size: 2rem;
  line-height: 2.6rem;
  padding: 1.1rem 0.9rem;
  position: absolute;
  z-index: 1;
  bottom: 1rem;
  right: 1rem;
`;
const Movie = ({ movie, ...rest }) => {
  console.log(movie);
  return (
    <div {...rest}>
      <MovieImg
        className="movie__img"
        style={{
          backgroundImage: `url(//image.tmdb.org/t/p/w300_and_h450_bestv2/${movie.poster_path})`,
        }}
      >
        <MovieVote className="movie__vote">{movie.vote_average}</MovieVote>
      </MovieImg>
      <div className="movie__bottom-container">
        <h3 className="movie__title">{movie.title}</h3>
        <h4 className="movie__date">
          {moment(movie.release_date).format("DD.MM.YYYY")}
        </h4>
      </div>
    </div>
  );
};

export default Movie;
